// Helper functions
// ═══════════════════════════════════════════════════════════════

export function getCityService(citySlug, serviceSlug) {
  return cityServices.find(
    (cs) => cs.citySlug === citySlug && cs.serviceSlug === serviceSlug
  );
}

export function getAllCityServiceParams() {
  return cityServices.map((cs) => ({
    city: cs.citySlug,
    service: cs.serviceSlug,
  }));
}

export function getSiblingServices(citySlug, currentServiceSlug) {
  return cityServices.filter(
    (cs) => cs.citySlug === citySlug && cs.serviceSlug !== currentServiceSlug
  );
}

export function getSameCities(currentCitySlug, serviceSlug) {
  return cityServices.filter(
    (cs) => cs.serviceSlug === serviceSlug && cs.citySlug !== currentCitySlug
  );
}